	private void exportData(boolean isSave){
	try{
		toData(isSave);
		FileNameExtensionFilter imgfilt2;
		exportData.setDialogTitle("Save Data");
		String expfilename;
		if(!isSave){
			imgfilt2 = new FileNameExtensionFilter("Exe Data (.exedata)","exedata");	
			expfilename = cb.getSelectedItem().toString()+".exedata";
			exportData.setSelectedFile(new File(expfilename));
		}else{
			imgfilt2 = new FileNameExtensionFilter("Save Data (.savedata)","savedata");	
			expfilename = cb.getSelectedItem().toString()+".savdata";
			exportData.setSelectedFile(new File(expfilename));

		}
		exportData.setFileFilter(imgfilt2);
		exportData.setApproveButtonText("Save");
		exportData.showOpenDialog(null);
		File expFile = exportData.getSelectedFile();
		if(!expFile.getPath().equals(expfilename)){
			exportData.setCurrentDirectory(expFile);
			Advapi32Util.registrySetStringValue(WinReg.HKEY_CURRENT_USER, "SOFTWARE\\GhostEditor", "pathExport", expFile.getPath());
		
			FileChannel out = new FileOutputStream(expFile.getParent()+"\\"+expFile.getName()).getChannel();
		
			ByteBuffer exe = ByteBuffer.allocate(528);
			exe.put(data.get(cb.getSelectedIndex()));
			exe.flip();
			out.write(exe);
			out.close();
		}
		}catch(Exception e){
			e.printStackTrace();
		}
	}
	
